import * as React from "react";
import { styled, useTheme } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Divider from "@mui/material/Divider";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import CardActionArea from "@mui/material/CardActionArea";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import { Parallax, ParallaxProvider } from "react-scroll-parallax";
import AutoGraphIcon from "@mui/icons-material/AutoGraph";
import Typography from "@mui/material/Typography";
import MainframeIcon from "../assets/images/mainframeicon.png";
import GraphIcon from "../assets/images/graph.png";
import LockIcon from "../assets/images/lock.png";
import TargetIcon from "../assets/images/target.png";
import Laptop from "../assets/images/laptop.png";
import { Container } from "@mui/material";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
// import gear5 from "./assets/images/gear5.jpg";
export default function TitlebarBelowImageList() {
  const theme = useTheme();

  return (
    <Container maxWidth="lg">
      <Box
        sx={{
          margin: "auto",
        }}
      >
        <Divider />
        <Box
          sx={{
            marginBottom: "100px",
            marginTop: "100px",
            marginLeft: "auto",
            marginRight: "auto",
          }}
        >
          <Box sx={{ width: "90%", margin: "auto" }}>
            <Typography variant="h3">What is zSIEM?</Typography>
            <Box sx={{ m: "10px" }}>
              <Typography variant="subtitle" color="text.secondary">
                zSIEM helps clients collect, in real-time, information
                pertaining to security events, database related events,
                transaction processing events and more. The technology
                pinpoints, filters, collects, reports mainframe events and then
                makes the events available to all major SIEM technologies.
              </Typography>
            </Box>
          </Box>
          <Box sx={{ marginTop: "50px" }}>
            <Grid
              container
              spacing={{ xs: 6, md: 3 }}
              columns={{ xs: 1, sm: 8, md: 12 }}
              margin="auto"
              align="center"
            >
              <Grid item xs={12} sm={6} md={3}>
                <Card
                  elevation="6"
                  sx={{
                    borderRadius: 3,
                    maxWidth: 345,
                    margin: "auto",
                  }}
                >
                  <CardMedia
                    component="img"
                    sx={{ width: 125, margin: "auto", pt: "10px" }}
                    image={GraphIcon}
                    alt="Live from space album cover"
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      SIEM Growth
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Enterprises that are implementing Security Information and
                      Event Management (SIEM) realize the importance of
                      visibility into the mainframe.
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
              <Grid item xs={2} sm={6} md={3}>
                <Card
                  elevation="6"
                  sx={{
                    borderRadius: 3,
                    maxWidth: 345,
                    margin: "auto",
                  }}
                >
                  <CardMedia
                    component="img"
                    sx={{ width: 125, margin: "auto", pt: "10px" }}
                    image={MainframeIcon}
                    alt="Live from space album cover"
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      Mainframe Market
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Enterprises are struggling to find and keep zOS resources.
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
              <Grid item xs={2} sm={6} md={3}>
                <Card
                  elevation="6"
                  sx={{
                    borderRadius: 3,
                    maxWidth: 345,
                    margin: "auto",
                  }}
                >
                  <CardMedia
                    component="img"
                    sx={{ width: 125, margin: "auto", pt: "10px" }}
                    image={TargetIcon}
                    alt="Live from space album cover"
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      Mission Critical
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Companies that have an IBM mainframe platform run their
                      most mission critical workload and store their most
                      valuable data on the mainframe.
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
              <Grid item xs={2} sm={6} md={3}>
                <Card
                  elevation="6"
                  sx={{
                    borderRadius: 3,
                    maxWidth: 345,
                    margin: "auto",
                  }}
                >
                  <CardMedia
                    component="img"
                    sx={{ width: 125, margin: "auto", pt: "10px" }}
                    image={LockIcon}
                    alt="Live from space album cover"
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      Compliance
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Enterprises that are implementing Security Information and
                      Event Management (SIEM) realize the importance of
                      visibility into the mainframe.
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
          </Box>
        </Box>

        <Divider />
      </Box>
      <Box
        sx={{
          marginBottom: "100px",
          marginTop: "100px",
          marginLeft: "auto",
          marginRight: "auto",
        }}
      >
        <Grid container spacing={2}>
          <Grid xs={12} md={4}>
            <Card padding="100px" flex="end" elevation="0">
              <CardMedia component="img" image={Laptop}></CardMedia>
            </Card>
          </Grid>
          <Grid pl="50px" align="left" xs={12} md={8}>
            <Typography variant="h3">What zSIEM monitors:</Typography>
            <Grid container spacing={2}>
              <Grid item xs={6} md={6}>
                <Typography align="left" variant="body1" color="text.secondary">
                  Changes to sensitive load libraries
                </Typography>
              </Grid>
              <Grid item xs={6} md={6}>
                <Typography align="left" variant="body1" color="text.secondary">
                  When a new version of mainframe software is added{" "}
                </Typography>
              </Grid>
              <Grid item xs={6} md={6}>
                <Typography align="left" variant="body1" color="text.secondary">
                  Security violations{" "}
                </Typography>
              </Grid>
              <Grid item xs={6} md={6}>
                <Typography align="left" variant="body1" color="text.secondary">
                  Critical DB2 table activity{" "}
                </Typography>
              </Grid>
              <Grid item xs={6} md={6}>
                <Typography align="left" variant="body1" color="text.secondary">
                  Special CICS transactions{" "}
                </Typography>
              </Grid>
              <Grid item xs={6} md={6}>
                <Typography align="left" variant="body1" color="text.secondary">
                  Abnormal FTP activity{" "}
                </Typography>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
}

